public:
    bool canPlaceFlowers(vector&lt;int&gt;&amp; flowerbed, int n) {
        int m = flowerbed.size();
        for (int i = 0; i &lt; m; ++i) {
            int l = i == 0 ? 0 : flowerbed[i - 1];
            int r = i == m - 1 ? 0 : flowerbed[i + 1];
            if (l + flowerbed[i] + r == 0) {
                flowerbed[i] = 1;
                --n;
            }
        }
        return n &lt;= 0;
    }
};

