    public String reverseStr(String s, int k) {
        char[] chart = s.toCharArray();


        for (int i=0 ; i&lt;chart.length ; i += 2*k){
            int left = i;
            int right = i+k-1;
            right = Math.min(chart.length-1 , i+k-1);

            while (left &lt; right){
                char tem = chart[left];
                chart[left] = chart[right];
